import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.for-each.js";
import "core-js/modules/es.iterator.map.js";
import "core-js/modules/es.iterator.reduce.js";
import "core-js/modules/es.iterator.some.js";
import Chart from '@/components/Chart.vue';
export default {
  name: 'AdminDashboard',
  components: {
    Chart
  },
  data() {
    return {
      user: {},
      activeTab: 'overview',
      showUserMenu: false,
      showAssignModal: false,
      showCreateOrder: false,
      showCreateTechnician: false,
      showOrderDetailModal: false,
      selectedOrder: null,
      selectedOrderDetail: null,
      orderFilter: '',
      userSearchTerm: '',
      isLoading: false,
      allOrders: [],
      technicians: [],
      users: [],
      dashboardStats: {
        totalOrders: 0,
        pendingOrders: 0,
        completedOrders: 0,
        activeTechnicians: 0
      },
      statistics: {
        totalRevenue: 0,
        laborCost: 0,
        materialCost: 0,
        technicianStats: [],
        negativeFeedback: []
      },
      statisticsDateRange: {
        start: '',
        end: ''
      },
      assignForm: {
        technicianIds: [],
        laborCost: 0,
        materialCost: 0
      }
    };
  },
  computed: {
    isManager() {
      return this.user.role === 'MANAGER';
    },
    isSuperAdmin() {
      return this.user.role === 'SUPER_ADMIN';
    },
    filteredOrders() {
      if (!this.orderFilter) return this.allOrders;
      return this.allOrders.filter(order => order.status === this.orderFilter);
    },
    filteredUsers() {
      if (!this.userSearchTerm) return this.users;
      return this.users.filter(user => user.name.toLowerCase().includes(this.userSearchTerm.toLowerCase()) || user.username.toLowerCase().includes(this.userSearchTerm.toLowerCase()));
    },
    recentOrders() {
      return this.allOrders.slice(0, 10);
    },
    availableTechnicians() {
      return this.technicians.filter(tech => tech.status !== 'INACTIVE');
    },
    totalCost() {
      return (parseFloat(this.assignForm.laborCost) || 0) + (parseFloat(this.assignForm.materialCost) || 0);
    },
    // 图表数据计算属性
    revenueChartData() {
      const laborCost = this.statistics.laborCost || 0;
      const materialCost = this.statistics.materialCost || 0;
      if (laborCost === 0 && materialCost === 0) {
        return [{
          label: '暂无数据',
          value: 1
        }];
      }
      return [{
        label: '工时费',
        value: laborCost
      }, {
        label: '材料费',
        value: materialCost
      }];
    },
    technicianTasksChartData() {
      if (!this.statistics.technicianStats || this.statistics.technicianStats.length === 0) {
        return [{
          label: '暂无数据',
          value: 0
        }];
      }
      return this.statistics.technicianStats.map(tech => ({
        label: tech.technicianName,
        value: tech.completedTasks
      }));
    },
    technicianEarningsChartData() {
      if (!this.statistics.technicianStats || this.statistics.technicianStats.length === 0) {
        return [{
          label: '暂无数据',
          value: 0
        }];
      }
      return this.statistics.technicianStats.map(tech => ({
        label: tech.technicianName,
        value: tech.totalEarnings
      }));
    },
    orderStatusChartData() {
      const statusCounts = {
        PENDING: 0,
        ASSIGNED: 0,
        IN_PROGRESS: 0,
        COMPLETED: 0,
        CANCELLED: 0
      };
      this.allOrders.forEach(order => {
        if (statusCounts.hasOwnProperty(order.status)) {
          statusCounts[order.status]++;
        }
      });
      const data = Object.entries(statusCounts).filter(([status, count]) => count > 0).map(([status, count]) => ({
        label: this.getStatusText(status),
        value: count
      }));
      return data.length > 0 ? data : [{
        label: '暂无数据',
        value: 1
      }];
    }
  },
  created() {
    this.loadUserInfo();
    this.loadData();
  },
  methods: {
    loadUserInfo() {
      const userData = localStorage.getItem('user');
      if (userData) {
        this.user = JSON.parse(userData);
        console.log('加载的管理员数据:', this.user);
        if (!this.user.id) {
          console.error('管理员数据中缺少ID字段:', this.user);
          this.$emit('message', '用户数据错误，请重新登录', 'error');
          this.logout();
        }
      } else {
        console.error('localStorage中没有管理员数据');
        this.$emit('message', '未找到用户信息，请重新登录', 'error');
        this.logout();
      }
    },
    async loadData() {
      try {
        this.isLoading = true;

        // 先加载基础数据
        await this.loadOrders();
        await this.loadTechnicians();
        if (this.isSuperAdmin) {
          await this.loadUsers();
        }

        // 然后基于基础数据计算统计信息
        await this.loadDashboardStats();

        // 如果是超级管理员，初始化统计分析数据
        if (this.isSuperAdmin) {
          this.calculateLocalStatistics();
        }
      } catch (error) {
        console.error('加载数据失败:', error);
        this.$emit('message', '加载数据失败', 'error');
      } finally {
        this.isLoading = false;
      }
    },
    async refreshData() {
      this.$emit('message', '正在刷新数据...', 'info');
      await this.loadData();
      this.$emit('message', '数据刷新完成', 'success');
    },
    async loadOrders() {
      try {
        const response = await this.$axios.get('/repair-orders');
        this.allOrders = Array.isArray(response.data) ? response.data : [];
        console.log('从API加载的订单数据:', this.allOrders);
        console.log('订单数量:', this.allOrders.length);
      } catch (error) {
        console.error('加载订单失败:', error);
        console.error('错误详情:', error.response?.data || error.message);
        this.allOrders = [];
        this.$emit('message', '加载订单数据失败: ' + (error.response?.data?.message || error.message), 'error');
      }
    },
    async loadTechnicians() {
      try {
        const response = await this.$axios.get('/technicians');
        this.technicians = Array.isArray(response.data) ? response.data : [];
        console.log('从API加载的技师数据:', this.technicians);
        console.log('技师数量:', this.technicians.length);
      } catch (error) {
        console.error('加载技师失败:', error);
        console.error('错误详情:', error.response?.data || error.message);
        this.technicians = [];
        this.$emit('message', '加载技师数据失败: ' + (error.response?.data?.message || error.message), 'error');
      }
    },
    async loadUsers() {
      try {
        const response = await this.$axios.get('/users');
        this.users = Array.isArray(response.data) ? response.data : [];
        console.log('加载的用户数据:', this.users);
      } catch (error) {
        console.error('加载用户失败:', error);
        this.users = [];
      }
    },
    async loadDashboardStats() {
      try {
        // 尝试从API获取统计数据
        const response = await this.$axios.get('/admins/dashboard-stats');
        this.dashboardStats = response.data;
        console.log('从API加载的统计数据:', this.dashboardStats);
      } catch (error) {
        console.error('加载统计数据失败，使用本地计算:', error);
        // 确保数据已初始化
        const orders = Array.isArray(this.allOrders) ? this.allOrders : [];
        const techs = Array.isArray(this.technicians) ? this.technicians : [];

        // 基于本地数据计算统计信息
        this.dashboardStats = {
          totalOrders: orders.length,
          pendingOrders: orders.filter(o => o.status === 'PENDING').length,
          completedOrders: orders.filter(o => o.status === 'COMPLETED').length,
          activeTechnicians: techs.length // 移除状态过滤，因为模拟数据没有status字段
        };
        console.log('本地计算的统计数据:', this.dashboardStats);
      }
    },
    async loadStatistics() {
      if (!this.isSuperAdmin) return;
      try {
        // 尝试从API加载各种统计数据
        const [revenueStats, techStats, feedbackStats] = await Promise.all([this.$axios.get('/repair-orders/analysis/monthly', {
          params: {
            year: 2024,
            month: 12
          }
        }).catch(() => null), this.$axios.get('/repair-orders/task-statistics', {
          params: {
            startDate: this.statisticsDateRange.start,
            endDate: this.statisticsDateRange.end
          }
        }).catch(() => null), this.$axios.get('/repair-orders/negative-feedback').catch(() => null)]);

        // 如果API调用失败，基于本地数据计算统计信息
        if (!revenueStats || !techStats || !feedbackStats) {
          this.calculateLocalStatistics();
        } else {
          this.statistics = {
            totalRevenue: revenueStats.data?.total_cost || 0,
            laborCost: revenueStats.data?.total_labor_cost || 0,
            materialCost: revenueStats.data?.total_material_cost || 0,
            technicianStats: techStats.data || [],
            negativeFeedback: feedbackStats.data || []
          };
        }
      } catch (error) {
        console.error('加载统计数据失败，使用本地计算:', error);
        this.calculateLocalStatistics();
      }
    },
    async calculateLocalStatistics() {
      // 确保数据已初始化
      if (!Array.isArray(this.allOrders)) {
        this.allOrders = [];
      }
      if (!Array.isArray(this.technicians)) {
        this.technicians = [];
      }

      // 基于本地订单数据计算统计信息
      const completedOrders = this.allOrders.filter(o => o.status === 'COMPLETED');

      // 计算收入统计
      const totalRevenue = completedOrders.reduce((sum, order) => sum + (order.totalCost || 0), 0);
      const laborCost = completedOrders.reduce((sum, order) => sum + (order.laborCost || 0), 0);
      const materialCost = completedOrders.reduce((sum, order) => sum + (order.materialCost || 0), 0);

      // 计算技师统计
      const technicianStats = this.technicians.map(tech => {
        const techOrders = completedOrders.filter(order => order.technicians && order.technicians.some(t => t.id === tech.id));
        return {
          technicianId: tech.id,
          technicianName: tech.name,
          skillType: tech.skillType,
          completedTasks: techOrders.length,
          totalEarnings: techOrders.reduce((sum, order) => sum + (order.laborCost || 0), 0),
          averageRating: this.calculateAverageRating(techOrders)
        };
      });

      // 获取真实的负面反馈数据
      await this.loadNegativeFeedback();
      this.statistics = {
        totalRevenue,
        laborCost,
        materialCost,
        technicianStats,
        negativeFeedback: this.statistics.negativeFeedback || []
      };
    },
    calculateAverageRating(orders) {
      const ratingsOrders = orders.filter(order => order.rating && order.rating > 0);
      if (ratingsOrders.length === 0) return 0;
      const totalRating = ratingsOrders.reduce((sum, order) => sum + order.rating, 0);
      return (totalRating / ratingsOrders.length).toFixed(1);
    },
    async loadNegativeFeedback() {
      try {
        const response = await this.$axios.get('/admins/negative-feedback', {
          params: {
            rating: 3
          } // 3分以下为负面反馈
        });
        this.statistics.negativeFeedback = response.data;
      } catch (error) {
        console.error('加载负面反馈失败:', error);
        this.statistics.negativeFeedback = [];
      }
    },
    assignOrder(order) {
      this.selectedOrder = order;
      this.assignForm = {
        technicianIds: [],
        laborCost: 0,
        materialCost: 0
      };
      this.showAssignModal = true;
    },
    async confirmAssign() {
      try {
        const updateData = {
          technicianIds: this.assignForm.technicianIds,
          laborCost: parseFloat(this.assignForm.laborCost),
          materialCost: parseFloat(this.assignForm.materialCost),
          totalCost: this.totalCost,
          status: 'ASSIGNED'
        };
        await this.$axios.put(`/repair-orders/${this.selectedOrder.id}`, updateData);

        // 更新本地数据
        const orderIndex = this.allOrders.findIndex(o => o.id === this.selectedOrder.id);
        if (orderIndex !== -1) {
          this.allOrders[orderIndex] = {
            ...this.allOrders[orderIndex],
            ...updateData
          };
        }
        this.closeAssignModal();
        this.$emit('message', '订单分配成功', 'success');
        // 重新加载数据以更新统计信息
        await this.loadData();
      } catch (error) {
        console.error('分配订单失败:', error);
        this.$emit('message', '分配订单失败', 'error');
      }
    },
    closeAssignModal() {
      this.showAssignModal = false;
      this.selectedOrder = null;
    },
    async viewOrderDetail(order) {
      try {
        // 获取订单详细信息
        const response = await this.$axios.get(`/repair-orders/${order.id}`);
        this.selectedOrderDetail = response.data;
        this.showOrderDetailModal = true;
      } catch (error) {
        console.error('获取订单详情失败:', error);
        this.$emit('message', '获取订单详情失败', 'error');
      }
    },
    closeOrderDetailModal() {
      this.showOrderDetailModal = false;
      this.selectedOrderDetail = null;
    },
    editOrder(order) {
      // 实现订单编辑
      this.$emit('message', '订单编辑功能开发中', 'info');
    },
    editTechnician(technician) {
      // 实现技师编辑
      this.$emit('message', '技师编辑功能开发中', 'info');
    },
    async deleteTechnician(technician) {
      if (confirm(`确定要删除技师 ${technician.name} 吗？此操作不可撤销。`)) {
        try {
          await this.$axios.delete(`/technicians/${technician.id}`);
          this.$emit('message', '技师删除成功', 'success');
          // 重新加载技师列表
          await this.loadTechnicians();
          // 重新加载统计数据
          await this.loadDashboardStats();
        } catch (error) {
          console.error('删除技师失败:', error);
          this.$emit('message', '删除技师失败: ' + (error.response?.data?.message || error.message), 'error');
        }
      }
    },
    viewUserDetail(user) {
      // 实现用户详情查看
      this.$emit('message', '用户详情功能开发中', 'info');
    },
    toggleUserMenu() {
      this.showUserMenu = !this.showUserMenu;
    },
    getRoleName(role) {
      const roleMap = {
        'MANAGER': '经理',
        'SUPER_ADMIN': '超级管理员'
      };
      return roleMap[role] || role;
    },
    getStatusText(status) {
      const statusMap = {
        'PENDING': '待处理',
        'ASSIGNED': '已分配',
        'IN_PROGRESS': '进行中',
        'COMPLETED': '已完成',
        'CANCELLED': '已取消'
      };
      return statusMap[status] || status;
    },
    getSkillTypeName(skillType) {
      const skillMap = {
        'MECHANIC': '机械维修',
        'ELECTRICIAN': '电气维修',
        'BODY_WORK': '车身维修',
        'PAINT': '喷漆',
        'DIAGNOSTIC': '诊断'
      };
      return skillMap[skillType] || skillType;
    },
    getVehicleDisplay(order) {
      if (order.vehicle) {
        const {
          licensePlate,
          brand,
          model,
          year
        } = order.vehicle;
        if (licensePlate && brand && model) {
          return `${licensePlate} - ${brand} ${model}${year ? ` (${year})` : ''}`;
        } else if (licensePlate) {
          return licensePlate;
        }
      }
      return '未知车辆';
    },
    formatDate(dateString) {
      return new Date(dateString).toLocaleDateString('zh-CN');
    },
    logout() {
      localStorage.removeItem('user');
      localStorage.removeItem('userRole');
      this.$router.push('/');
    }
  }
};
