      }
    },
    async loadStatistics() {
      try {
        // 基于实际任务数据计算统计信息
        const totalTasks = this.allTasks.length;
        const completedTasks = this.allTasks.filter(task => task.status === 'COMPLETED').length;
        const pendingTasks = this.allTasks.filter(task => task.status === 'ASSIGNED' || task.status === 'IN_PROGRESS').length;
        this.statistics = {
          totalTasks,
          completedTasks,
          pendingTasks,
          averageRating: 4.8 // 这个需要从反馈数据计算
        };
      } catch (error) {
        console.error('计算统计信息失败:', error);
        this.statistics = {
          totalTasks: 0,
          completedTasks: 0,
          pendingTasks: 0,
          averageRating: 0
        };
      }
    },
    async loadEarnings() {
      try {
        // 基于完成的任务计算收入
        const completedTasks = this.allTasks.filter(task => task.status === 'COMPLETED');
        const totalEarnings = completedTasks.reduce((sum, task) => sum + (task.laborCost || 0), 0);

        // 计算本月收入（简化版本）
        const currentMonth = new Date().getMonth();
        const monthlyTasks = completedTasks.filter(task => {
          const taskMonth = new Date(task.completedAt || task.createdAt).getMonth();
          return taskMonth === currentMonth;
        });
        const monthlyEarnings = monthlyTasks.reduce((sum, task) => sum + (task.laborCost || 0), 0);
        this.earnings = {
          monthly: monthlyEarnings,
          total: totalEarnings,
          averagePerTask: completedTasks.length > 0 ? totalEarnings / completedTasks.length : 0
        };
      } catch (error) {
        console.error('计算收入失败:', error);
        this.earnings = {
          monthly: 0,
          total: 0,
          averagePerTask: 0
        };
      }
