          params: {
            status: 'IN_PROGRESS'
          }
        });

        // 更新本地任务状态
        const taskIndex = this.allTasks.findIndex(t => t.id === task.id);
        if (taskIndex !== -1) {
          this.allTasks[taskIndex].status = 'IN_PROGRESS';
          this.allTasks[taskIndex].updatedAt = new Date().toISOString();
