import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.reduce.js";
export default {
  name: 'CustomerDashboard',
  data() {
    return {
      user: {},
      activeTab: 'overview',
      showUserMenu: false,
      showAddVehicle: false,
      showCreateOrder: false,
      showOrderDetail: false,
      showFeedback: false,
      vehicles: [],
      repairOrders: [],
      feedbacks: [],
      statistics: {
        vehicleCount: 0,
        repairCount: 0,
        pendingCount: 0,
        totalCost: 0
      },
      recentOrders: [],
      vehicleForm: {
        licensePlate: '',
        brand: '',
        model: '',
        year: '',
        color: '',
        vin: ''
      },
      profileForm: {},
      repairOrderForm: {
        vehicleId: '',
        description: '',
        preferredDate: '',
        contactPhone: ''
      },
      selectedOrder: null,
      feedbackOrder: null,
      feedbackForm: {
        rating: 0,
        comment: ''
      },
      hoverRating: 0,
      isSubmitting: false
    };
  },
  created() {
    this.loadUserInfo();
    this.loadData();
  },
  methods: {
    loadUserInfo() {
      const userData = localStorage.getItem('user');
      if (userData) {
        this.user = JSON.parse(userData);
        this.profileForm = {
          ...this.user
        };
        console.log('加载的用户数据:', this.user);

        // 检查用户ID是否存在
        if (!this.user.id) {
          console.error('用户数据中缺少ID字段:', this.user);
          this.$emit('message', '用户数据错误，请重新登录', 'error');
          this.logout();
        }
      } else {
        console.error('localStorage中没有用户数据');
        this.$emit('message', '未找到用户信息，请重新登录', 'error');
        this.logout();
      }
    },
    async loadData() {
      try {
        await Promise.all([this.loadVehicles(), this.loadRepairOrders(), this.loadFeedbacks()]);
        this.calculateStatistics();
      } catch (error) {
        console.error('加载数据失败:', error);
        this.$emit('message', '加载数据失败', 'error');
      }
    },
    async loadVehicles() {
      try {
        const response = await this.$axios.get(`/vehicles/user/${this.user.id}`);
        this.vehicles = response.data;
      } catch (error) {
        console.error('加载车辆失败:', error);
      }
    },
    async loadRepairOrders() {
      try {
        const response = await this.$axios.get(`/repair-orders/user/${this.user.id}`);
        this.repairOrders = response.data;
        this.recentOrders = this.repairOrders.slice(0, 5);
      } catch (error) {
        console.error('加载维修记录失败:', error);
      }
    },
    async loadFeedbacks() {
      try {
        const response = await this.$axios.get(`/feedbacks/user/${this.user.id}`);
        this.feedbacks = response.data;
      } catch (error) {
        console.error('加载反馈失败:', error);
      }
    },
    calculateStatistics() {
      this.statistics = {
        vehicleCount: this.vehicles.length,
        repairCount: this.repairOrders.length,
        pendingCount: this.repairOrders.filter(order => order.status === 'PENDING' || order.status === 'IN_PROGRESS').length,
        totalCost: this.repairOrders.reduce((sum, order) => sum + (order.actualCost || order.estimatedCost || 0), 0)
      };
    },
    toggleUserMenu() {
      this.showUserMenu = !this.showUserMenu;
    },
    async addVehicle() {
      try {
        const vehicleData = {
          ...this.vehicleForm,
          userId: this.user.id
        };
        await this.$axios.post('/vehicles', vehicleData);
        this.showAddVehicle = false;
        this.vehicleForm = {
          licensePlate: '',
          brand: '',
          model: '',
          year: '',
          color: '',
          vin: ''
        };
        this.loadVehicles();
        this.$emit('message', '车辆添加成功', 'success');
      } catch (error) {
        console.error('添加车辆失败:', error);
        this.$emit('message', '添加车辆失败', 'error');
      }
    },
    async updateProfile() {
      try {
        // 检查用户ID是否存在
        if (!this.user.id) {
          throw new Error('用户ID不存在，请重新登录');
        }
        console.log('更新用户资料，用户ID:', this.user.id);

        // 构建完整的用户更新请求数据
        const updateData = {
          username: this.user.username,
          // 保持原用户名
          password: this.user.password || '',
          // 如果没有密码字段，发送空字符串
          name: this.profileForm.name,
          phone: this.profileForm.phone,
          email: this.profileForm.email,
          address: this.profileForm.address,
          vehicles: this.user.vehicles || [],
          repairOrders: this.user.repairOrders || []
        };
        console.log('发送的更新数据:', updateData);
        const response = await this.$axios.put(`/users/${this.user.id}`, updateData);
        this.user = {
          ...this.user,
          ...response.data
        };
        this.profileForm = {
          ...this.user
        };
        localStorage.setItem('user', JSON.stringify(this.user));
        this.$emit('message', '个人资料更新成功', 'success');
      } catch (error) {
        console.error('更新资料失败:', error);
        const errorMessage = error.response?.data?.message || error.message || '更新资料失败';
        this.$emit('message', errorMessage, 'error');
      }
    },
    getStatusText(status) {
      const statusMap = {
        'PENDING': '待处理',
        'IN_PROGRESS': '进行中',
        'COMPLETED': '已完成',
        'CANCELLED': '已取消'
      };
      return statusMap[status] || status;
    },
    formatDate(dateString) {
      return new Date(dateString).toLocaleDateString('zh-CN');
    },
    editVehicle(vehicle) {
      // 实现编辑车辆功能
      this.$emit('message', '编辑功能开发中', 'info');
    },
    async deleteVehicle(vehicleId) {
      if (confirm('确定要删除这辆车吗？')) {
        try {
          await this.$axios.delete(`/vehicles/${vehicleId}`);
          this.loadVehicles();
          this.$emit('message', '车辆删除成功', 'success');
        } catch (error) {
          console.error('删除车辆失败:', error);
          this.$emit('message', '删除车辆失败', 'error');
        }
      }
    },
    createRepairOrder(vehicle) {
      this.repairOrderForm.vehicleId = vehicle.id;
      this.showCreateOrder = true;
    },
    async createRepairOrder() {
      try {
        this.isSubmitting = true;
        const response = await this.$axios.post('/repair-orders', this.repairOrderForm);
        this.repairOrders.push(response.data);
        this.showCreateOrder = false;
        this.repairOrderForm = {
          vehicleId: '',
          description: '',
          preferredDate: '',
          contactPhone: ''
        };
        this.$emit('message', '预约维修成功', 'success');
      } catch (error) {
        console.error('预约维修失败:', error);
        this.$emit('message', '预约维修失败', 'error');
      } finally {
        this.isSubmitting = false;
      }
    },
    viewOrderDetail(order) {
      this.selectedOrder = order;
      this.showOrderDetail = true;
    },
    addFeedback(order) {
      this.feedbackOrder = order;
      this.showFeedback = true;
    },
    async submitFeedback() {
      try {
        this.isSubmitting = true;
        const response = await this.$axios.post('/feedbacks', this.feedbackForm);
        this.feedbacks.push(response.data);
        this.showFeedback = false;
        this.feedbackForm = {
          rating: 0,
          comment: ''
        };
        this.$emit('message', '反馈提交成功', 'success');
      } catch (error) {
        console.error('提交反馈失败:', error);
        this.$emit('message', '提交反馈失败', 'error');
      } finally {
        this.isSubmitting = false;
      }
    },
    logout() {
      localStorage.removeItem('user');
      localStorage.removeItem('userRole');
      this.$router.push('/');
    }
  }
};
